public class AutoSUm {

    public static void main(String[] args) {
        String s1 = "";
        String s2 = "";


        int min = Math.min(s1.length(), s2.length());

        String s3 = "";
        String s4 = "";
        String s5 = "";

        if (min == s1.length()) {
            s3 = s2.substring(s2.length() - s1.length());
            s4 = s1;
            s5 = s2.substring(0, s2.length() - s1.length());

        } else {
            s3 = s1.substring(s1.length() - s2.length());
            s5 = s1.substring(0, s1.length() - s2.length());
            s4 = s2;
        }


        int a0 = 0;
        String resp = "";
        for (int i = 0; i < min; i++) {
            int a = Integer.parseInt(String.valueOf(s3.charAt(min - i - 1)));
            int b = Integer.parseInt(String.valueOf(s4.charAt(min - i - 1)));
            ReturnCLass returnCLass = sum(a, b, a0);
            int b1 = returnCLass.b;
            int a1 = returnCLass.a;
            a0 = a1;
            resp += b1;


        }
        String resp2 = "";
        int length = s5.length();
        for (int i = 0; i < length; i++) {
            int a = Integer.parseInt(String.valueOf(s5.charAt(length - i - 1)));
            int b = 0;
            ReturnCLass returnCLass = sum(a, b, a0);
            int b1 = returnCLass.b;
            int a1 = returnCLass.a;
            a0 = a1;
            resp2 += b1;
            if (a0 == 0)
                break;
        }
        String finish = "";
        if (a0 == 1)
            finish = a0 + s5.substring(0, s5.length() - resp2.length()) + reverseString(resp2) + reverseString(resp);

        else
            finish = s5.substring(0, s5.length() - resp2.length()) + reverseString(resp2) + reverseString(resp);



        System.out.println(finish);


    }

    private static String reverseString(String resp) {
        String resps = "";
        for (int i = 0; i < resp.length(); i++) {
            resps += resp.charAt(resp.length() - 1 - i);

        }
        return resps;
    }


    private static ReturnCLass sum(int a, int b, int c) {
        int i = a + b + c;

        String cstring = String.valueOf(i);

        if (cstring.length() == 2) {
            ReturnCLass returnCLass = new ReturnCLass();
            returnCLass.a = Integer.parseInt(String.valueOf(cstring.charAt(0)));
            returnCLass.b = Integer.parseInt(String.valueOf(cstring.charAt(1)));
            return returnCLass;
        } else {
            ReturnCLass returnCLass = new ReturnCLass();
            returnCLass.a = 0;
            returnCLass.b = Integer.parseInt(String.valueOf(cstring.charAt(0)));
            return returnCLass;

        }
    }


}
